mapper.select:
SELECT article.id, article.regDate, article.updateDate, article.title, article.body, article.memberId, article.boardId, board.id, member.name AS 'name' FROM article INNER JOIN `member` ON article.memberId = member.id INNER JOIN board ON article.boardId = board.id WHERE board.id = #{boardId}
AND title LIKE CONCAT('%', #{searchKeyword}, '%')
AND body LIKE CONCAT('%', #{searchKeyword}, '%')
AND (title LIKE CONCAT('%', #{searchKeyword}, '%') OR body LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY article.id DESC LIMIT #{i}, #{itemsPerPage}

mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate= NOW()
WHERE id = #{id}
