mapper.update:
UPDATE reply
`body` = #{body}, updateDate = NOW()
WHERE id = #{id}

UPDATE reply
SET goodReactionPoint = goodReactionPoint + 1
WHERE id = #{relId}

UPDATE reply
SET goodReactionPoint = goodReactionPoint - 1
WHERE id = #{relId}

UPDATE reply
SET badReactionPoint = badReactionPoint + 1
WHERE id = #{relId}

UPDATE reply
SET badReactionPoint = badReactionPoint - 1
WHERE id = #{relId}
